mapper.select:
SELECT t.id, t.login_name AS loginName, t.name, t.phone, t.password, t.sex, t.age, t.create_time AS createTime, t.user_type AS userType, t.status, t.organization_id AS organizationId, s.name AS organizationName, listagg(o.name, ',') within group (order by o.name) AS rolesList FROM C_USER t LEFT JOIN C_USER_role r ON t.id = r.user_id LEFT JOIN c_role o ON r.role_id = o.id LEFT JOIN c_organization s ON s.id = t.organization_id
t.name = #{name}
t.organization_id = #{organizationId}
and t.create_time >= #{startTime}
and t.create_time <= #{endTime}
GROUP BY t.id, t.login_name, t.name, t.phone, t.password, t.sex, t.age, t.create_time, t.user_type, t.status, t.organization_id, s.name
