mapper.select:
select * from (
        select a.id, a.bus_num, a.bus_platform,a.agent_id,a.bus_status,b.ag_name,b.id as agnetid,b.ag_uniq_num,a.clo_review_status,b.ag_status ,rownum as row_count
        from a_agent_businfo a left join a_agent b on a.agent_id = b.id

          and a.bus_platform = #{par.busPlatform}
           and  b.ag_name like CONCAT(CONCAT('%',#{par.search_name}),'%')
           and a.clo_review_status = #{par.clo_review_status}
          and b.ag_status = #{par.ag_status}
          and b.c_incom_status  = #{par.c_incom_status}
          and a.bus_num = #{par.bus_num}
          and a.status = 1
          and b.status = 1
          and rownum <= #{par.page.from} + #{par.page.size}

        order by a.c_time desc
      ) datalist

         and  datalist.row_count > #{par.page.from}

select
  count(a.id)
from a_agent_businfo a left join a_agent b on a.agent_id = b.id

  and a.bus_platform = #{par.busPlatform}
   and  b.ag_name like CONCAT(CONCAT('%',#{par.search_name}),'%')
   and a.clo_review_status = #{par.clo_review_status}
  and b.ag_status = #{par.ag_status}
  and b.c_incom_status  = #{par.c_incom_status}
  and a.bus_num = #{par.bus_num}
  and a.status = 1
  and b.status = 1


select
  b.id,b.agent_id,b.bus_num,b.bus_platform,b.bus_type,a.ag_uniq_num,a.ag_name,a.ag_nature,a.ag_head,
  a.ag_head_mobile,b.bus_status,b.status,b.clo_review_status,a.ag_status,b.c_time
from A_agent a,A_agent_businfo b
where a.id=b.agent_id
and ag_status = #{reqMap.agStatus}
and b.c_user = #{reqMap.cUser}

  and a.ag_name  like CONCAT(CONCAT('%',#{reqMap.agName}),'%')


  and a.ag_uniq_num = #{reqMap.agUniqNum}


  and b.bus_num = #{reqMap.busNum}


  and b.bus_platform = #{reqMap.busPlatform}


  and b.clo_review_status = #{reqMap.cloReviewStatus}


  and b.id = #{reqMap.id}

and b.status = #{reqMap.status}
order by b.c_time desc

select
   count(*)
from A_agent a,A_agent_businfo b
where a.id=b.agent_id
and ag_status = #{reqMap.agStatus}
and b.c_user = #{reqMap.cUser}

  and a.ag_name = #{reqMap.agName}


  and a.ag_uniq_num = #{reqMap.agUniqNum}


  and b.bus_num = #{reqMap.busNum}


  and b.bus_platform = #{reqMap.busPlatform}


  and b.clo_review_status = #{reqMap.cloReviewStatus}

and b.status = #{reqMap.status}
order by b.c_time desc

select
A.ID,A.AGENT_ID,A.BUS_NUM,A.BUS_PLATFORM,A.BUS_TYPE,A.BUS_PARENT,
B.AG_UNIQ_NUM,B.AG_NAME,C.PLATFORM_NAME,D.D_ITEMNAME
from
a_agent_businfo a
left join a_agent b on a.agent_id=b.id
left join a_platform c on c.platform_num = a.bus_platform
left join d_dict d on d.d_group='AGENT' AND d.d_artifact = 'BUS_TYPE' and d.d_itemvalue = a.bus_type

     a.status=1 and b.status=1 and a.bus_status=1
     and a.id=#{id}
    and a.bus_platform =#{busPlatform}
    and a.agent_id = #{agentId}
    and a.bus_parent= #{busParent}
