mapper.sql:
select t.ID,t.AGENT_ID,t.FREEZE_STATUS,t.FREEZE_PERSON, t.FREEZE_DATE,t.FREEZE_CAUSE,t.UNFREEZE_PERSON, t.UNFREEZE_DATE,t.UNFREEZE_CAUSE,t.FREEZE_NUM, t.REMARK,t.STATUS,t.VERSION,nvl(t.FREEZE_TYPE,'1') as FREEZE_TYPE, a.ag_name,a.c_incom_time,to_char(a.freestatus) as FREESTATUS from a_agent_freeze t left join a_agent a on a.id=t.agent_id
and t.status = 1 and a.status = 1
and t.id = #{map.id,jdbcType=VARCHAR}
and t.agent_Id in
#{listItem}
and a.ag_name LIKE CONCAT(CONCAT('%',#{map.agentName}),'%')
and a.c_incom_time >= to_date(#{map.incomTimeBegin}, 'yy-mm-dd hh24:mi:ss')
and a.c_incom_time <= to_date(#{map.incomTimeEnd}, 'yy-mm-dd hh24:mi:ss')
and t.freeze_Date >= to_date(#{map.freezeDateBegin}, 'yy-mm-dd hh24:mi:ss')
and t.freeze_Date <= to_date(#{map.freezeDateEnd}, 'yy-mm-dd hh24:mi:ss')
and t.unFreeze_Date >= to_date(#{map.unFreezeDateBegin}, 'yy-mm-dd hh24:mi:ss')
and t.unFreeze_Date <= to_date(#{map.unFreezeDateEnd}, 'yy-mm-dd hh24:mi:ss')
and a.freestatus = #{map.freeStatus,jdbcType=VARCHAR}
and t.freeze_Cause = #{map.freezeCause,jdbcType=VARCHAR}
and t.freeze_Status = #{map.freezeStatus,jdbcType=VARCHAR}
order by t.agent_id,t.freeze_date desc
