mapper.select:
select * from ( select row_.*, rownum rownum_ from (
select
distinct
A_APPROVAL_FLOW_RECORD.ID, A_APPROVAL_FLOW_RECORD.TASK_ID, A_APPROVAL_FLOW_RECORD.EXECUTION_ID, A_APPROVAL_FLOW_RECORD.TASK_NAME, A_APPROVAL_FLOW_RECORD.BUS_ID, A_APPROVAL_FLOW_RECORD.BUS_TYPE, A_APPROVAL_FLOW_RECORD.APPROVAL_PERSON, A_APPROVAL_FLOW_RECORD.APPROVAL_DEP, A_APPROVAL_FLOW_RECORD.APPROVAL_TIME, A_APPROVAL_FLOW_RECORD.APPROVAL_RESULT, A_APPROVAL_FLOW_RECORD.APPROVAL_OPINION, A_APPROVAL_FLOW_RECORD.STATUS, A_APPROVAL_FLOW_RECORD.VERSION, A_APPROVAL_FLOW_RECORD.ACTIVITY_STATUS, A_APPROVAL_FLOW_RECORD.ERROR_MSG, A_APPROVAL_FLOW_RECORD.AGENT_ID, A_APPROVAL_FLOW_RECORD.AGENT_NAME, TO_CHAR(a_bus_act_rel.c_time,'yyyy-MM-dd') as SUBMITDATE from A_APPROVAL_FLOW_RECORD left join a_bus_act_rel on A_APPROVAL_FLOW_RECORD.EXECUTION_ID = a_bus_act_rel.activ_id
and A_APPROVAL_FLOW_RECORD.${criterion.condition}
and A_APPROVAL_FLOW_RECORD.${criterion.condition} #{criterion.value}
and A_APPROVAL_FLOW_RECORD.${criterion.condition} #{criterion.value} and #{criterion.secondValue}
and A_APPROVAL_FLOW_RECORD.${criterion.condition}
#{listItem}
and to_char(a_bus_act_rel.c_time,'yyyy-MM-dd') >= #{subMitDateSta} and to_char(a_bus_act_rel.c_time,'yyyy-MM-dd') <= #{subMitDateEnd}
order by ${ApprovalFlowRecordExample.orderByClause}
) row_ ) where rownum_ > #{ApprovalFlowRecordExample.page.begin} and rownum_ <= #{ApprovalFlowRecordExample.page.end}

select
count(*)
from A_APPROVAL_FLOW_RECORD
left join a_bus_act_rel on A_APPROVAL_FLOW_RECORD.EXECUTION_ID = a_bus_act_rel.activ_id








                and A_APPROVAL_FLOW_RECORD.${criterion.condition}


                and A_APPROVAL_FLOW_RECORD.${criterion.condition} #{criterion.value}


                and A_APPROVAL_FLOW_RECORD.${criterion.condition} #{criterion.value} and #{criterion.secondValue}


                and A_APPROVAL_FLOW_RECORD.${criterion.condition}

                  #{listItem}








      and to_char(a_bus_act_rel.c_time,'yyyy-MM-dd') >= #{subMitDateSta} and to_char(a_bus_act_rel.c_time,'yyyy-MM-dd') <= #{subMitDateEnd}

( select distinct ar.id, ar.bus_type as approval_bus_type, to_char(ar.approval_time,'YYYY-MM-DD') as approval_time, cu.name as approval_person, (select d.d_itemname from d_dict d where d.d_group='AGENT_AUDIT' and d.d_artifact='APPROVAL_TYPE' and d.d_itemvalue = ar.approval_result and d.d_status=1) as approval_result, ar.approval_opinion, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='AG_STATUS_S' and d.d_itemvalue = ac.activ_status and d.d_status=1) as activ_status, ar.agent_id, ar.bus_id, ar.agent_name, ab.bus_num, dq.name as ag_doc_district, sq.name as ag_doc_pro, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab.bus_type and d.d_status=1) as bus_type, ap.platform_name as bus_platform, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_SCOPE' and d.d_itemvalue = ab.bus_scope and d.d_status=1) as bus_scope, ab.bus_login_num, ab.bus_activation_parent, aa_one.ag_name as one_parent_name, ab_one.bus_num as one_parent_num, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab_one.bus_type and d.d_status=1) as one_parent_type, ap_one.platform_name as one_parent_platform, aa_two.ag_name as two_parent_name, ab_two.bus_num as two_parent_num, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab_two.bus_type and d.d_status=1) as two_parent_type, ap_two.platform_name as two_parent_platform, aa_three.ag_name as three_parent_name, ab_three.bus_num as three_parent_num, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab_three.bus_type and d.d_status=1) as three_parent_type, ap_three.platform_name as three_parent_platform from a_approval_flow_record ar left join c_user cu on cu.id = ar.approval_person left join a_bus_act_rel ac on ar.execution_id = ac.activ_id left join a_agent aa on aa.id = ar.agent_id and aa.status=1 left join a_agent_businfo ab on ab.agent_id = aa.id and ab.status=1 left join a_agent_businfo ab_one on ab_one.id = ab.bus_parent and ab_one.status=1 left join a_agent_businfo ab_two on ab_two.id = ab_one.bus_parent and ab_two.status=1 left join a_agent_businfo ab_three on ab_three.id = ab_two.bus_parent and ab_three.status=1 left join a_agent aa_one on aa_one.id = ab_one.agent_id and aa_one.status=1 left join a_agent aa_two on aa_two.id = ab_two.agent_id and aa_two.status=1 left join a_agent aa_three on aa_three.id = ab_three.agent_id and aa_three.status=1 left join c_organization dq on dq.id = ab.ag_doc_district left join c_organization sq on sq.id = ab.ag_doc_pro left join a_platform ap on ap.platform_num = ab.bus_platform and ap.status=1 left join a_platform ap_one on ap_one.platform_num = ab_one.bus_platform and ap_one.status=1 left join a_platform ap_two on ap_two.platform_num = ab_two.bus_platform and ap_two.status=1 left join a_platform ap_three on ap_three.platform_num = ab_three.bus_platform and ap_three.status=1
and ar.status=1 and ar.approval_person = #{approvalPerson} and ar.bus_id = #{busId} and ar.bus_type = #{busType} and ar.approval_result = #{approvalResult} and ar.agent_id = #{agentId} and ar.agent_name like #{agentName}
and to_char(ar.approval_time,'yyyy-MM-dd') >= #{beginTime} and to_char(ar.approval_time,'yyyy-MM-dd') <= #{endTime}
and to_char(ac.c_time,'yyyy-MM-dd') >= #{subMitDateSta} and to_char(ac.c_time,'yyyy-MM-dd') <= #{subMitDateEnd}
) order by approval_time desc

( select distinct ar.id, ar.bus_type as approval_bus_type, to_char(ar.approval_time,'YYYY-MM-DD') as approval_time, cu.name as approval_person, (select d.d_itemname from d_dict d where d.d_group='AGENT_AUDIT' and d.d_artifact='APPROVAL_TYPE' and d.d_itemvalue = ar.approval_result and d.d_status=1) as approval_result, ar.approval_opinion, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='AG_STATUS_S' and d.d_itemvalue = ac.activ_status and d.d_status=1) as activ_status, ar.agent_id, ar.bus_id, ar.agent_name, ab.bus_num, dq.name as ag_doc_district, sq.name as ag_doc_pro, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab.bus_type and d.d_status=1) as bus_type, ap.platform_name as bus_platform, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_SCOPE' and d.d_itemvalue = ab.bus_scope and d.d_status=1) as bus_scope, ab.bus_login_num, ab.bus_activation_parent, aa_one.ag_name as one_parent_name, ab_one.bus_num as one_parent_num, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab_one.bus_type and d.d_status=1) as one_parent_type, ap_one.platform_name as one_parent_platform, aa_two.ag_name as two_parent_name, ab_two.bus_num as two_parent_num, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab_two.bus_type and d.d_status=1) as two_parent_type, ap_two.platform_name as two_parent_platform, aa_three.ag_name as three_parent_name, ab_three.bus_num as three_parent_num, (select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='BUS_TYPE' and d.d_itemvalue = ab_three.bus_type and d.d_status=1) as three_parent_type, ap_three.platform_name as three_parent_platform from a_approval_flow_record ar left join c_user cu on cu.id = ar.approval_person left join a_bus_act_rel ac on ar.execution_id = ac.activ_id left join a_agent_businfo ab on ab.id = ar.bus_id and ab.status=1 left join a_agent_businfo ab_one on ab_one.id = ab.bus_parent and ab_one.status=1 left join a_agent_businfo ab_two on ab_two.id = ab_one.bus_parent and ab_two.status=1 left join a_agent_businfo ab_three on ab_three.id = ab_two.bus_parent and ab_three.status=1 left join a_agent aa on aa.id = ar.agent_id and aa.status=1 left join a_agent aa_one on aa_one.id = ab_one.agent_id and aa_one.status=1 left join a_agent aa_two on aa_two.id = ab_two.agent_id and aa_two.status=1 left join a_agent aa_three on aa_three.id = ab_three.agent_id and aa_three.status=1 left join c_organization dq on dq.id = ab.ag_doc_district left join c_organization sq on sq.id = ab.ag_doc_pro left join a_platform ap on ap.platform_num = ab.bus_platform and ap.status=1 left join a_platform ap_one on ap_one.platform_num = ab_one.bus_platform and ap_one.status=1 left join a_platform ap_two on ap_two.platform_num = ab_two.bus_platform and ap_two.status=1 left join a_platform ap_three on ap_three.platform_num = ab_three.bus_platform and ap_three.status=1
and ar.status=1 and ar.approval_person = #{approvalPerson} and ar.bus_id = #{busId} and ar.bus_type = #{busType} and ar.approval_result = #{approvalResult} and ar.agent_id = #{agentId} and ar.agent_name like #{agentName}
and to_char(ar.approval_time,'yyyy-MM-dd') >= #{beginTime} and to_char(ar.approval_time,'yyyy-MM-dd') <= #{endTime}
and to_char(ac.c_time,'yyyy-MM-dd') >= #{subMitDateSta} and to_char(ac.c_time,'yyyy-MM-dd') <= #{subMitDateEnd}
) order by approval_time desc

(
select
distinct ar.id,
ar.bus_type as approval_bus_type,
to_char(ar.approval_time,'YYYY-MM-DD') as approval_time,
cu.name as approval_person,
(select d.d_itemname from d_dict d where d.d_group='AGENT_AUDIT' and d.d_artifact='APPROVAL_TYPE'
and d.d_itemvalue = ar.approval_result and d.d_status=1) as approval_result,
ar.approval_opinion,
(select d.d_itemname from d_dict d where d.d_group='AGENT' and d.d_artifact='AG_STATUS_S'
and d.d_itemvalue = ac.activ_status and d.d_status=1) as activ_status,
ar.agent_id,
ar.bus_id,
ar.agent_name
from a_approval_flow_record ar
left join c_user cu on cu.id = ar.approval_person
left join a_bus_act_rel ac on ar.execution_id = ac.activ_id

   and ar.status=1 
   and ar.approval_person = #{approvalPerson} 
   and ar.bus_id = #{busId} 
   and ar.bus_type = #{busType} 
   and ar.approval_result = #{approvalResult} 
   and ar.agent_id = #{agentId} 
   and ar.agent_name like #{agentName} 

    and to_char(ar.approval_time,'yyyy-MM-dd') >= #{beginTime} and to_char(ar.approval_time,'yyyy-MM-dd') <= #{endTime}


    and to_char(ac.c_time,'yyyy-MM-dd') >= #{subMitDateSta} and to_char(ac.c_time,'yyyy-MM-dd') <= #{subMitDateEnd}


)
order by approval_time desc
