mapper.select:
select cap.*
from A_CAPITAL cap left join o_Payment_detail detail on cap.id=detail.payment_id
where 1=1 and cap.status=1 and cap.id=#{paymentId}

select a.id as agent_id, a.ag_name, pa.id as parent_agent_id, pa.ag_name as parent_agent_name, c.c_type, sum(c_amount) as sum_amt,sum(c_fq_in_amount) as ky_amt,sum(freeze_amt) as sum_freeze_amt from A_capital c left join a_agent a on a.id = c.c_agent_id and a.status = 1 and a.ag_status = 'Approved' left join a_agent_businfo ai on a.id = ai.agent_id and ai.status = 1 and ai.clo_review_status = 3 left join a_agent_businfo aib on ai.bus_parent = aib.id and aib.status = 1 and aib.clo_review_status = 3 left join a_agent pa on pa.id = aib.agent_id and pa.status = 1 and pa.ag_status = 'Approved'
c.status = 1 and c.clo_review_status =3 and a.ID = #{ID} and a.AG_NAME = #{AG_NAME}
group by a.id, a.ag_name, pa.id, pa.ag_name, c.c_type

select count(1) from( select a.id as agent_id, a.ag_name, pa.id as parent_agent_id, pa.ag_name as parent_agent_name, c.c_type, sum(c_fq_in_amount) as sum_amount from A_capital c left join a_agent a on a.id = c.c_agent_id and a.status = 1 and a.ag_status = 'Approved' left join a_agent_businfo ai on a.id = ai.agent_id and ai.status = 1 and ai.clo_review_status = 3 left join a_agent_businfo aib on ai.bus_parent = aib.id and aib.status = 1 and aib.clo_review_status = 3 left join a_agent pa on pa.id = aib.agent_id and pa.status = 1 and pa.ag_status = 'Approved'
c.status = 1 and c.clo_review_status =3 and a.ID = #{ID} and a.AG_NAME = #{AG_NAME}
group by a.id, a.ag_name, pa.id, pa.ag_name, c.c_type )
