mapper.select:
select ACT_CODE,max(o_activity.id)as ID,max(o_activity.activity_name)as ACTIVITYNAME,max(o_activity.price) as PRICE from o_activity

  o_activity.status=1
   and o_activity.act_code!=#{notEqActcode} 
   and o_activity.vender=#{vender} 
   and o_activity.pro_model=#{proModel} 
   and o_activity.product_id=#{productId} 
   and o_activity.begin_time <= #{beginTime} 
   and o_activity.end_time >= #{endTime}

group by act_code
order by act_code desc

select * from o_activity a

  a.status=1
  and  a.act_code = (
    select d.act_code from o_sub_order b
    left join o_sub_order_activity c on b.id=c.sub_order_id
    left join o_activity d on d.id = c.activity_id

      b.status=1 and c.status=1 and rownum=1
       and b.order_id=#{orderId} 
       and b.pro_id=#{productId} 

  )
   and a.product_id=#{productId} 
   and a.begin_time <= #{beginTime} 
   and a.end_time >= #{endTime}
