mapper.select:
select
  ORDER_ID, LOGISTICS_ID, PRO_ID, PRO_NAME, SETTLEMENT_PRICE, ACTIVITY_ID, ACTIVITY_NAME, AGENT_ID,
  max(SN_NUM) as MAX_SN,min(SN_NUM) as MIN_SN,count(*) as PRO_NUM
from o_logistics_detail
where
sn_num >= #{snBegin}
and sn_num <= #{snEnd}
and status = #{status}
and RECORD_STATUS = #{recordStatus}

  and ACTIVITY_ID = #{activityId}

group by
  ORDER_ID, LOGISTICS_ID, PRO_ID, PRO_NAME, SETTLEMENT_PRICE, ACTIVITY_ID, ACTIVITY_NAME, AGENT_ID
