sql.where:
1=1
   AND R.PLAN_NUM = #{PLAN_NUM}
   AND R.ORDER_ID = #{ORDER_ID}
   AND O.AGENT_ID = #{AGENT_ID}
   AND R.return_order_detail_id = #{returnId}
    and (to_char(o.c_time,'YYYY-MM-DD') between #{beginTime} and #{endTime}) 
    and d.PAY_METHOD=#{payMethod} 
    and c.PLATFORM_NUM=#{platform} 
    and R.PRO_COM=#{proCom}
    and R.MODEL=#{model} 
    and AGENT.AG_UNIQ_NUM=#{agUniqNum} 
    and oso.ACTIVITY_NAME LIKE CONCAT(CONCAT('%',#{activityName}),'%')
    and os.PRO_TYPE=#{proType}

mapper.select:
select

from  o_logistics where
id = (
  select
    logistics_id
  from o_logistics_detail
  where
  sn_num >= #{snBegin}
  and sn_num <= #{snEnd}
  and status = #{status}
  group by logistics_id
)
and order_id = #{orderId}
and pro_com = #{proCom}
and pro_model = #{proModel}

mapper.update:
update o_logistics_detail
 set

        return_order_id = #{returnId,jdbcType=VARCHAR},

 STATUS = #{status,jdbcType=DECIMAL},
 RECORD_STATUS = #{recordStatus,jdbcType=DECIMAL}
where order_id =#{orderId,jdbcType=VARCHAR}
and  sn_num >= #{startSn,jdbcType=VARCHAR} and sn_num <= #{endSn,jdbcType=VARCHAR}
