mapper.select:
select ro.ID, ro.ORDER_ID, ro.RECEIPT_ID, ro.SEND_PRO_NUM, ro.SEND_DATE, ro.PRO_COM, ro.MODEL, ro.PLAN_PRO_NUM, oo.ID, oo.AGENT_ID, oo.O_APYTIME, rp.ID, rp.LOG_COM, rp.W_NUMBER, rp.SN_BEGIN_NUM, rp.SN_END_NUM, rp.SEND_NUM from O_RECEIPT_PLAN ro, O_LOGISTICS rp, O_ORDER oo where ro.ID = rp.RECEIPT_PLAN_ID and oo.ID = rp.ORDER_ID
AND ro.SEND_DATE = #{SEND_DATE} AND oo.O_APYTIME = #{O_APYTIME}
order by oo.O_APYTIME desc

select ro.ID, ro.ORDER_ID, ro.RECEIPT_ID, ro.SEND_PRO_NUM, ro.SEND_DATE, ro.PRO_COM, ro.MODEL, ro.PLAN_PRO_NUM, oo.ID, oo.AGENT_ID, oo.O_APYTIME, rp.ID, rp.LOG_COM, rp.W_NUMBER, rp.SN_BEGIN_NUM, rp.SN_END_NUM, rp.SEND_NUM from O_RECEIPT_PLAN ro, O_LOGISTICS rp, O_ORDER oo where ro.ID = rp.RECEIPT_PLAN_ID and oo.ID = rp.ORDER_ID
AND ro.SEND_DATE = #{SEND_DATE} AND oo.O_APYTIME = #{O_APYTIME}
order by oo.O_APYTIME desc
