mapper.select:
select o.id, o.o_num, o.order_platform, o.payment_method, o.o_amo, o.pay_amo, d.pay_amount, d.real_amount, d.outstanding_amount, d.pay_status, o.clear_status, o.review_status, o.order_status, o.o_apytime, o.c_time, o.remark, b.id as agentId, b.ag_name, nvl((select sum(o_payment_detail.pay_amount) from o_payment_detail where o_payment_detail.payment_id = d.id and o_payment_detail.order_id= o.id and o_payment_detail.payment_status in (1,3) ),0) as qk, c.platform_name from o_order o left join a_agent b on o.agent_id = b.id left join a_platform c on c.platform_num = o.order_platform left join o_payment d on d.order_id = o.id
and o.status = 1 and o.o_num = #{oNum} and o.agent_id = #{agentId} and o.order_platform = #{orderPlatform} and b.ag_uniq_num = #{agUniqNum} and b.ag_name like #{agName}
order by o.c_time desc

select
count(o.id)
from o_order o
left join a_agent b on o.agent_id = b.id
left join a_platform c on c.platform_num = o.order_platform
left join o_payment d on d.order_id = o.id

   and  o.o_num = #{oNum} 
   and  o.agent_id = #{agentId} 
   and o.order_platform =  #{orderPlatform} 
   and b.ag_uniq_num = #{agUniqNum} 
   and b.ag_name like  #{agName}
