mapper.select:
select count(id) from(
select
o.id
from o_order o
left join a_agent b on o.agent_id = b.id
left join a_platform c on c.platform_num = o.order_platform
left join o_payment d on d.order_id = o.id

  o.user_id=#{userId}
   and  o.status = 1 
   and  o.o_num = #{oNum} 
   and  o.id = #{id} 
   and  o.agent_id = #{agentId} 
   and o.order_platform =  #{orderPlatform} 
   and b.ag_uniq_num = #{agUniqNum} 
   and b.ag_name like  #{agName} 
   and (to_char(o.c_time,'YYYY-MM-DD') between #{beginTime} and #{endTime}) 
   and c.PLATFORM_NUM=#{platformNum} 
   and b.AG_UNIQ_NUM=#{agUniqNum} 
   and EXISTS(select * from O_SUB_ORDER where status=1
    and PRO_TYPE=#{proType} and o.id=ORDER_ID) 
   and EXISTS(select * from O_SUB_ORDER where status=1
    and model=#{model} and o.id=ORDER_ID) 
   and d.PAY_METHOD=#{payMethod} 
   and  EXISTS(select * FROM o_sub_order os left join o_sub_order_activity soa on soa.SUB_ORDER_ID=os.id
    and os.order_id=o.id where soa.VENDER=#{vender})

    and EXISTS (select * from o_sub_order_activity soa left join o_sub_order os on  soa.SUB_ORDER_ID=os.id
    and os.order_id=o.id where soa.ACTIVITY_NAME LIKE CONCAT(CONCAT('%',#{activityName}),'%'))


UNION
select
o.id
from o_order o
left join a_agent b on o.agent_id = b.id
left join a_platform c on c.platform_num = o.order_platform
left join o_payment d on d.order_id = o.id

   and 1=2 
   and (b.ag_doc_pro = #{doc,jdbcType=VARCHAR} or b.ag_doc_district=#{doc,jdbcType=VARCHAR}) 
   and  o.status = 1 
   and  o.o_num = #{oNum} 
   and  o.id = #{id} 
   and  o.agent_id = #{agentId} 
   and o.order_platform =  #{orderPlatform} 
   and b.ag_uniq_num = #{agUniqNum} 
   and b.ag_name like  #{agName} 
   and (to_char(o.c_time,'YYYY-MM-DD')  between #{beginTime} and #{endTime}) 
   and c.PLATFORM_NUM=#{platformNum} 
   and b.AG_UNIQ_NUM=#{agUniqNum} 
   and EXISTS(select * from O_SUB_ORDER where status=1
    and PRO_TYPE=#{proType} and o.id=ORDER_ID) 
   and EXISTS(select * from O_SUB_ORDER where status=1
    and model=#{model} and o.id=ORDER_ID) 
   and d.PAY_METHOD=#{payMethod} 
   and  EXISTS(select * FROM o_sub_order os left join o_sub_order_activity soa on soa.SUB_ORDER_ID=os.id
    and os.order_id=o.id where soa.VENDER=#{vender})

    and EXISTS (select * from o_sub_order_activity soa left join o_sub_order os on soa.SUB_ORDER_ID=os.id
    and os.order_id=o.id where soa.ACTIVITY_NAME LIKE CONCAT(CONCAT('%',#{activityName}),'%'))


)

select count(id) from(
select
o.id
from o_order o
left join a_agent b on o.agent_id = b.id
left join a_platform c on c.platform_num = o.order_platform
left join o_payment d on d.order_id = o.id

   and  o.status = 1 
   and  o.o_num = #{oNum} 
   and  o.id = #{id} 
   and  o.agent_id = #{agentId} 
   and o.order_platform =  #{orderPlatform} 
   and b.ag_uniq_num = #{agUniqNum} 
   and b.ag_name like  #{agName} 
   and (to_char(o.c_time,'YYYY-MM-DD') between #{beginTime} and #{endTime}) 

)
