mapper.select:
select count(a.id) from O_RETURN_ORDER_DETAIL a
left join o_return_order b on a.return_id = b.id
where
(
 a.begin_sn <= #{begin,jdbcType=VARCHAR}
 and a.end_sn >= #{begin,jdbcType=VARCHAR}
 and  b.ret_schedule in


           ${stsItem}


)
or
(
  a.begin_sn <= #{end,jdbcType=VARCHAR}
  and a.end_sn >= #{end,jdbcType=VARCHAR}
  and  b.ret_schedule in


      ${stsItem}


)
