select jo.id, jo.ag_id, jo.ag_name, jo.bus_num, jo.platform_name, one.jo_key_name as jo_first_key_num, two.jo_key_name as jo_second_key_num, jm3.jo_key_name as PRIORITY_LEVEL, jo.launch_time, jo.launch_person_name, jo.launch_branch_name, jo.launch_person_email, jo.jo_progress, jo.accept_group, jo.deal_time_start, jo.accept_now_group, jo.launch_person_id from jo_order jo left join jo_key_manage one on one.jo_key = jo.jo_first_key_num left join jo_key_manage two on two.jo_key = jo.jo_second_key_num LEFT JOIN jo_key_manage jm3 ON jm3.id = jo.priority_level
1 = 1 and jo.id = #{map.id} and jo.jo_first_key_num = #{map.joFirstKeyNum} and jo.jo_second_key_num like CONCAT(CONCAT('%',#{map.joSecondKeyNum}),'%') and jo.ag_name like CONCAT(CONCAT('%',#{map.agName}),'%') and jo.bus_num like CONCAT(CONCAT('%',#{map.busNum}),'%') and jo.priority_level = #{map.priorityLevel} and jo.jo_progress = #{map.joProgress} and jo.launch_branch_name = #{map.launchBranchName} and jo.launch_time between #{map.launchBeginTime} and #{map.launchEndTime} and jo.launch_person_id = #{map.launchPersonId} and jo.launch_branch_id = #{map.launchBranchId}
order by jo.launch_time desc
