mapper.update:
update P_BALANCE_APPROVAL set APPROVAL_STATUS = #{approvalStatus,jdbcType=VARCHAR}
where 1=1

  and BALANCE_ID = #{balanceId,jdbcType=VARCHAR}

   and BATCH_NO = #{batchNo,jdbcType=VARCHAR}

update P_BALANCE_APPROVAL set APPROVAL_STATUS = #{approvalStatus,jdbcType=VARCHAR}
where 1=1 and BATCH_NO = #{batchNo,jdbcType=VARCHAR}

  and APPROVAL_STATUS= #{originalStatus,jdbcType=VARCHAR}

mapper.select:
SELECT CASE WHEN PBA.REQUEST_NO = '77' THEN '第一次导入' WHEN PBA.REQUEST_NO = '88' THEN '第二次导入' WHEN PBA.REQUEST_NO = '99' THEN '第三次导入' ELSE '数据错误' END AS REQUEST_NO, CASE WHEN C.CLO_TYPE = '1' THEN '1' ELSE '0' END AS CLO_TYPE, PBA.BALANCE_ID, PBA.BATCH_NO, PBA.APPLY_DATE, PBA.APPLY_USER, PBA.APPLY_TIME, PBA.SETTLE_MONTH, PBA.AGENT_ID, PBA.AGENT_NAME, PBA.BUS_CODE, AP.PLATFORM_NAME AS BUS_NAME, PBA.BALANCE_AMT,PBA.REQUEST_NO AS REQUEST_NO_TEMP, NVL(PBA.BALANCE_ACCT_NO, CASE WHEN PBA.BUS_CODE = '6000' THEN AC600.BANKCODE ELSE C.CLO_BANK_ACCOUNT END) AS BALANCE_ACCT_NO, NVL(PBA.BALANCE_ACCT_NAME, CASE WHEN PBA.BUS_CODE = '6000' THEN AC600.ACCOUNTNAME ELSE C.CLO_REALNAME END) AS BALANCE_ACCT_NAME, NVL(PBA.BALANCE_BANK_NO, CASE WHEN PBA.BUS_CODE = '6000' THEN AC600.BANKID ELSE C.CLO_BANK_BRANCH END) AS BALANCE_BANK_NO, NVL(PBA.BALANCE_BANK_NAME, CASE WHEN PBA.BUS_CODE = '6000' THEN AC600.ACCOUNTBANK ELSE C.CLO_BANK END) AS BALANCE_BANK_NAME, PBA.APPROVAL_STATUS,PBA.REALITY_AG_ID, PBA.REALITY_AG_NAME,C.VARSION FROM P_BALANCE_APPROVAL PBA LEFT JOIN A_PLATFORM AP ON PBA.BUS_CODE = AP.PLATFORM_NUM LEFT JOIN A_AGENT_COLINFO C ON PBA.REALITY_AG_ID=C.AGENT_ID LEFT JOIN (SELECT A.AGENCYID,A.BANKCODE,A.ACCOUNTNAME,A.ACCOUNTBANK,A.BANKID FROM RYX_AGENCYBANKINFO A WHERE A.AGENCYID LIKE '600%' UNION ALL SELECT A.AGENCY_ID AGENCYID,A.BANKCODE,A.ACCOUNTNAME,A.ACCOUNTBANK,A.BANKID FROM QT1TBLAGENCY A WHERE NOT EXISTS ( SELECT 1 FROM RYX_AGENCYBANKINFO RABI WHERE RABI.AGENCYID LIKE '600%' AND RABI.AGENCYID=A.AGENCY_ID ) AND A.BANKID IS NOT NULL AND A.BANKCODE IS NOT NULL AND A.ACCOUNTNAME IS NOT NULL AND LENGTH(A.BANKID)=12 AND LENGTH(A.ACCOUNTNAME)>=2) AC600 ON PBA.REALITY_AG_ID = AC600.AGENCYID WHERE 1=1
and APPROVAL_STATUS != #{approvalStatus,jdbcType=VARCHAR}
and PBA.AGENT_ID = #{agentId,jdbcType=VARCHAR}
and AGENT_NAME = #{agentName,jdbcType=VARCHAR}
and SETTLE_MONTH = #{settleMonth,jdbcType=VARCHAR}
and BUS_CODE = #{busCode,jdbcType=VARCHAR}
and REQUEST_NO = #{requestNo,jdbcType=VARCHAR}
and BALANCE_ID = #{balanceId,jdbcType=VARCHAR}
and BATCH_NO = #{batchNo,jdbcType=VARCHAR}
