mapper.select:
select pcd.ID,pcd.AGENT_ID,pcd.AGENT_NAME,pcd.PARENT_AGENT_ID,pcd.PARENT_AGENT_NAME, pcd.REMARK,pcd.DEDUCTION_REMARK,pcd.APPLICATION_MONTH,pcd.APPLICATION_AMT,pcd.APPLICATION_TYPE, pcd.CREATE_DATE,pcd.CREATE_NAME,pd.DEDUCTION_STATUS,pd.NOT_DEDUCTION_AMT, pcd.APPLICATION_STATUS from P_CITYAPPLICATION_DETAIL pcd left join P_DEDUCTION pd on pcd.ID = pd.ID
pcd.APPLICATION_TYPE = #{map.type} and pcd.CREATE_NAME=#{map.userId}
and pcd.AGENT_ID = #{map.agentId}
and pcd.AGENT_NAME = #{map.agentName}
order by pcd.CREATE_DATE desc

select
count(*)
from P_CITYAPPLICATION_DETAIL pcd
left join P_DEDUCTION pd on pcd.ID = pd.ID

  pcd.APPLICATION_TYPE = #{type} and pcd.CREATE_NAME=#{userId}

    and pcd.AGENT_ID = #{agentId}


    and pcd.AGENT_NAME = #{agentName}
