insert into pms_profit (MONTH, UNIQUE_FLAG, AGENT_ID, SHEET_NAME,
SHEET_COLUMN, SHEET_HEAD, SHEET_DATA, PROFIT_TYPE, PROFIT_HZ,
PAY_CONDITION, STATUS, FREE_REASON, PAY_TRAN_MONEY, IMPORT_TIME,
IMPORT_PERSON, UPDATE_TIME, UPDATE_PERSON, FREE_TIME, PAY_TIME,sheet_Order,order_number,ID,BUS_CODE,BUS_NAME)
values (#{month,jdbcType=VARCHAR},#{ uniqueFlag,jdbcType=VARCHAR},#{
agentId,jdbcType=VARCHAR},#{ sheetName,jdbcType=CLOB},#{
sheetColumn,jdbcType=INTEGER}, xmlType((
select sheet_head from PMS_PROFIT_temp where month=#{month,jdbcType=VARCHAR}
and order_Number=#{orderNumber,jdbcType=INTEGER}
  and sheet_name=#{sheetName,jdbcType=VARCHAR} and IMPORT_PERSON=#{importPerson,jdbcType=VARCHAR}
 )), xmlType((
select sheet_data from PMS_PROFIT_temp where month=#{month,jdbcType=VARCHAR}
and order_Number=#{orderNumber,jdbcType=INTEGER}
  and sheet_name=#{sheetName,jdbcType=VARCHAR} and IMPORT_PERSON=#{importPerson,jdbcType=VARCHAR}
 )),#{ profitType,jdbcType=VARCHAR},#{
profitHz,jdbcType=VARCHAR},#{ payCondition,jdbcType=VARCHAR},#{
status,jdbcType=VARCHAR},#{ freeReason,jdbcType=VARCHAR},#{
payTranMoney,jdbcType=VARCHAR},#{ importTime,jdbcType=VARCHAR},#{
importPerson,jdbcType=VARCHAR},#{ updateTime,jdbcType=VARCHAR},#{
updatePerson,jdbcType=VARCHAR},#{ freeTime,jdbcType=VARCHAR},#{
payTime,jdbcType=VARCHAR},#{sheetOrder,jdbcType=INTEGER},#{orderNumber,jdbcType=INTEGER},#{ id,jdbcType=VARCHAR},#{busCode,jdbcType=VARCHAR},#{busName,jdbcType=VARCHAR})
