sql.where:
1=1
 and PROFIT_POS_DATE = #{PROFIT_POS_DATE} 
 and POS_AGENT_ID = #{POS_AGENT_ID} 
 and POS_AGENT_NAME = #{POS_AGENT_NAME} 
 and POS_MECHANISM_TYPE = #{POS_MECHANISM_TYPE} 
 and POS_MECHANISM_ID = #{POS_MECHANISM_ID} 
 and POS_COMPARE_COUNT = #{POS_COMPARE_COUNT} 
 and POS_CURRENT_COUNT = #{POS_CURRENT_COUNT} 
 and POS_COMPARE_LOAN_COUNT = #{POS_COMPARE_LOAN_COUNT} 
 and POS_CURRENT_LOAN_COUNT = #{POS_CURRENT_LOAN_COUNT} 
 and POS_AMT = #{POS_AMT} 
 and POS_STANDARD = #{POS_STANDARD} 
 and POS_OWN_REWARD = #{POS_OWN_REWARD} 
 and POS_DOWN_REWARD = #{POS_DOWN_REWARD} 
 and POS_REAWRD_PROFIT = #{POS_REAWRD_PROFIT} 
 and POS_REMARK = #{POS_REMARK}

mapper.select:

select 
       ,(select info2.AGENT_ID from A_AGENT_BUSINFO info2 where ID=
       (select BUS_PARENT from A_AGENT_BUSINFO info1 where info1.AGENT_ID=detail.POS_AGENT_ID and
               ( BUS_NUM like 'SS%' or BUS_NUM like 'O%'))) as PARENT_AGENT_ID ,(select agent.AG_NAME from A_AGENT agent where agent.ID=(select info2.AGENT_ID from A_AGENT_BUSINFO info2 where ID=
       (select BUS_PARENT from A_AGENT_BUSINFO info1 where info1.AGENT_ID=detail.POS_AGENT_ID and
               ( BUS_NUM like 'SS%' or BUS_NUM like 'O%'))))as PARENT_AGENT_NAME from POS_REWARD_DETAIL detail
 where 1=1

  and POS_AGENT_ID = #{POS_AGENT_ID} 
  and POS_AGENT_NAME = #{POS_AGENT_NAME} 
  and POS_MECHANISM_TYPE = #{POS_MECHANISM_TYPE} 
  and POS_REMARK = #{POS_REMARK}

mapper.update:
UPDATE POS_REWARD_DETAIL  SET
POS_OWN_REWARD = 0, POS_DOWN_REWARD = 0, POS_REAWRD_PROFIT = 0
where PROFIT_POS_DATE = #{profitDate,jdbcType=VARCHAR}
