mapper.select:

select

from P_TAX_DEDUCTION_DETAIL
START WITH PROFIT_MONTH=#{detail.profitMonth}

  AND AGENT_ID=#{detail.agentId}


  AND AGENT_NAME=#{detail.agentName}

CONNECT BY  PRIOR  AGENT_ID=PARENT_AGENT_ID AND PROFIT_MONTH=#{detail.profitMonth}

select  count(1) from (
  select
  distinct

  from P_TAX_DEDUCTION_DETAIL
  START WITH PROFIT_MONTH=#{profitMonth}

    AND AGENT_ID=#{agentId}


    AND AGENT_NAME=#{agentName}

  CONNECT BY  PRIOR  AGENT_ID=PARENT_AGENT_ID AND PROFIT_MONTH=#{profitMonth}
)
